mapper.insert:
INSERT INTO qft_focus_housing (
		creater_id
		,create_time
		,creater_name
		,updater_id
		,update_time
		,company_id
		,focus_type
		,store_id
		,store_name

		,group_id

		,house_code
		,house_area
		,property_adrr

		,building


		,developers


		,developers_call


		,developers_other_call


		,transfer_accounts


		,account_number


		,transfer_bank


		,floor


		,is_lift


		,room_count


		,house_note

)
VALUES (
		#{createrId}
		,#{createTime}
		,#{createrName}
		,#{updaterId}
		,#{updateTime}
		,#{companyId}
		,#{focusType}
		,#{storeId}
		,#{storeName}

		,#{groupId}

		,#{houseCode}
		,#{houseArea}
		,#{propertyAdrr}

		,#{building}


		,#{developers}


		,#{developersCall}


		,#{developersOtherCall}


		,#{transferAccounts}


		,#{accountNumber}


		,#{transferBank}


		,#{floor}


		,#{isLift}


		,#{roomCount}


		,#{houseNote}

) 
ON DUPLICATE KEY UPDATE
		updater_id = #{updaterId}
		,update_time = #{updateTime}
		,focus_type = #{focusType}
		,store_id = #{storeId}
		,store_name = #{storeName}

		,group_id = #{groupId}

		,house_code = #{houseCode}
		,house_area = #{houseArea}
		,property_adrr = #{propertyAdrr}

		,building = #{building}


		,developers = #{developers}


	,developers_call = #{developersCall}


		,developers_other_call = #{developersOtherCall}


		,transfer_accounts = #{transferAccounts}


		,account_number = #{accountNumber}


		,transfer_bank = #{transferBank}


		,floor = #{floor}


		,is_lift = #{isLift}


		,room_count = #{roomCount}


		,house_note = #{houseNote}

mapper.select:
SELECT h.creater_id createrId, h.create_time createTime, h.creater_name createrName h.updater_id updaterId, h.update_time updateTime, h.company_id companyId, h.focus_type focusType, h.store_id storeId, h.store_name storeName, h.group_id groupId, h.house_code houseCode, h.house_area houseArea, h.property_adrr propertyAdrr, h.building, h.developers, h.developers_call developersCall, h.developers_other_call developersOtherCall, h.transfer_accounts transferAccounts, h.account_number accountNumber, h.transfer_bank transferBank, h.floor, h.is_lift isLift, h.room_count roomCount, h.house_note houseNote, t.hosting_create_time hostingCreateTime, t.hosting_end_time hostingEndTime, t.contract_year contractYear, t.contract_month contractMonth, t.contract_day contractDay, t.joe_price joePrice, t.payment_method paymentMethod, t.vacancy_year vacancyYear, t.vacancy_month vacancyMonth, t.vacamcy_day vacamcyDay FROM qft_focus_housing h, qft_focus_trust t WHERE h.is_delete = 0 AND h.id = t.housing_id
AND h.company_id = #{companyId}
ORDER BY h.update_time DESC
