mapper.select:
select 
	id as id,
	dept_name as name,
	company_id as companyId,
	parent_id as pId,
	create_time as createTime
from qft_company_dept
where is_delete = 0

	and	id = #{id}


	and	dept_name = #{deptName}


	and	parent_id = #{parentId}


	and	company_id = #{companyId}

select concat('company-',c.id) as id, c.companyname as name, "" as pId from tb_company c where 1=1
and c.id = #{companyId}
union all select concat('dept-',t.id) as id, t.dept_name as name, concat('company-',t.company_id) as pId from qft_company_dept t where t.is_delete = 0
and t.company_id = #{companyId}
union all select concat('position-',p.id) as id, p.name as name, concat('dept-',p.dept_id) as pId from qft_company_position p LEFT JOIN qft_company_dept d on p.dept_id = d.id where p.is_delete = 0
and d.company_id = #{companyId}
